{
  "name": "@dark-factory/core",
  "version": "0.1.0",
  "private": true,
  "description": "Dark Factory core service (Bun + Elysia)",
  "scripts": {
    "dev": "bun run --watch src/index.ts",
    "start": "bun run src/index.ts"
  },
  "dependencies": {
    "adze": "^2.3.0",
    "elysia": "latest",
    "elysia-protobuf": "github:victoryforphil/elysia-protobuf#master",
    "ts-proto": "^2.11.2"
  },
  "devDependencies": {
    "bun-types": "latest"
  },
  "module": "src/index.js"
}

import { Elysia } from "elysia";
import { config } from "./config";
import { logger } from "./logging";

const serviceName = "dark-factory-core";

const app = new Elysia()
  .get("/", () => ({
    service: serviceName,
    status: "ok",
    message: "Dark Factory core is running",
    concepts: ["product", "variant", "actor"],
    env: config.env,
  }))
  .get("/health", () => ({ status: "ok" }))
  .listen({
    hostname: config.http.address_listen,
    port: config.http.address_port,
  });

export const config: CoreConfig = {
  env: process.env.NODE_ENV || "development",
  http: {
    address_listen: Bun.env.LISTEN_ADDRESS ?? "127.0.0.1",
    address_port: Number(Bun.env.PORT ?? 4150),
  },
};

export interface CoreConfig {
  env: string;
  http: HTTPConfig;
}

export interface HTTPConfig {
  address_listen: string;
  address_port: number;
}

