syntax = "proto3";

package darkfactory.core.v1;

import "core/v1/types/actor.proto";
import "core/v1/types/common.proto";

// POST /actors/spawn
message SpawnActorRequest {
  string product_id = 1;
  string variant_id = 2;

  // Stage 0: ACTOR_KIND_OPENCODE_SESSION
  ActorKind kind = 3;

  map<string, string> metadata = 4;
}

message SpawnActorSuccess {
  Actor actor = 1;

syntax = "proto3";

package darkfactory.core.v1;

import "core/v1/types/common.proto";
import "core/v1/types/product.proto";
import "core/v1/types/variant.proto";

// POST /products
message CreateProductRequest {
  // Required. Format: @local://{abs_path}
  string product_locator = 1;
  optional string display_name = 2;
}

message CreateProductSuccess {
  Product product = 1;

  // Stage 0 invariant: creation also creates the default variant.
  Variant default_variant = 2;

syntax = "proto3";

package darkfactory.core.v1;

import "core/v1/types/common.proto";
import "google/protobuf/timestamp.proto";

message Actor {
  string id = 1;
  string product_id = 2;
  string variant_id = 3;

  ActorKind kind = 4;
  ActorStatus status = 5;

  string endpoint = 6;
  string transport = 7;
  int64 pid = 8;

  google.protobuf.Timestamp spawned_at = 9;

syntax = "proto3";

package darkfactory.core.v1;

import "google/protobuf/timestamp.proto";

message Product {
  // Deterministic ID: "prd_" + sha256_hex(canonical_product_locator).
  string id = 1;

  // Canonical format: @local://{abs_path}
  string locator = 2;

  optional string display_name = 3;
  string default_variant_id = 4;
  google.protobuf.Timestamp created_at = 5;
  google.protobuf.Timestamp updated_at = 6;
}

syntax = "proto3";

package darkfactory.core.v1;

import "google/protobuf/timestamp.proto";

message Variant {
  // Deterministic ID: "var_" + sha256_hex(canonical_variant_locator).
  string id = 1;

  string product_id = 2;

  // Canonical format: @local://{abs_path}#default
  string locator = 3;

  // Stage 0 invariant: only "default".
  string name = 4;

  google.protobuf.Timestamp created_at = 5;
}

syntax = "proto3";

package darkfactory.core.v1;

message ApiError {
  string code = 1;
  string message = 2;
  map<string, string> details = 3;
}

enum ActorKind {
  ACTOR_KIND_UNSPECIFIED = 0;
  ACTOR_KIND_OPENCODE_SESSION = 1;
}

enum ActorStatus {
  ACTOR_STATUS_UNSPECIFIED = 0;
  ACTOR_STATUS_STARTING = 1;
  ACTOR_STATUS_RUNNING = 2;
  ACTOR_STATUS_STOPPED = 3;

