#!/usr/bin/env bun
import { dirname, join, resolve } from "node:path";
import { existsSync } from "node:fs";

export function findRepoRoot(startDir: string = import.meta.dir): string {
  let current = resolve(startDir);

  while (true) {
    if (existsSync(join(current, ".git"))) {
      return current;
    }

    const parent = dirname(current);
    if (parent === current) {
      throw new Error(`Could not find repository root from: ${startDir}`);
    }

    current = parent;
  }
}

#!/usr/bin/env bun

export type CommandStep = {
  name: string;
  command: string;
  args?: string[];
  cwd?: string;
};

export async function runCommandSteps(steps: CommandStep[]): Promise<void> {
  for (const [index, step] of steps.entries()) {
    const stepNumber = index + 1;
    console.log(`Install // Step // ${stepNumber}/${steps.length} ${step.name}`);

    const process = Bun.spawn([step.command, ...(step.args ?? [])], {
      cwd: step.cwd,
      stdin: "inherit",
      stdout: "inherit",
      stderr: "inherit",
    });

