}

function extractLocUrls(xml: string): string[] {
  return Array.from(xml.matchAll(/<loc>(.*?)<\/loc>/g), (match) => decodeEntities(match[1]?.trim() ?? "")).filter(
    Boolean,
  );
}

function shouldIncludeUrl(url: string): boolean {
