}

function extractLocUrls(xml: string): string[] {
  return Array.from(xml.matchAll(/<loc>(.*?)<\/loc>/g), (match) =>
    decodeEntities(match[1]?.trim() ?? ""),
  ).filter(Boolean);
}

async function discoverDocsUrlsFromSitemap(sitemapUrl: string): Promise<string[]> {
  const visited = new Set<string>();
  const pending: string[] = [sitemapUrl];
  const docsUrls = new Set<string>();

  while (pending.length > 0) {
    const current = pending.pop();
    if (!current || visited.has(current)) {
      continue;
    }

    visited.add(current);

    let xml: string;
    try {
      xml = await fetchText(current);
    } catch {
      continue;
    }

    const locUrls = extractLocUrls(xml);
    for (const locUrl of locUrls) {
      if (locUrl.endsWith(".xml")) {
        if (!visited.has(locUrl)) {
          pending.push(locUrl);
        }
        continue;
      }

      if (!locUrl.startsWith(DOCS_ROOT)) {
        continue;
      }

      if (locUrl.includes("#")) {
        continue;
      }

      docsUrls.add(locUrl);
    }
  }

  return Array.from(docsUrls).sort((a, b) => a.localeCompare(b));
}

function isEnglishDocsUrl(url: string): boolean {
  const pathname = new URL(url).pathname;
  if (!pathname.startsWith("/docs")) {
    return false;
  }

  const relative = pathname.replace(/^\/docs\/?/, "");
  if (!relative) {
    return true;
  }

  const firstSegment = relative.split("/")[0]?.toLowerCase() ?? "";
  if (!firstSegment) {
    return true;
  }

  if (firstSegment === "en" || firstSegment === "english") {
    return true;
  }

  if (/^[a-z]{2}(?:-[a-z]{2})?$/.test(firstSegment)) {
    return false;
  }

  return true;
}

async function mapConcurrent<T, R>(
  values: T[],
  limit: number,
  map: (value: T, index: number) => Promise<R>,
): Promise<R[]> {
  const results = new Array<R>(values.length);
  let index = 0;

  async function worker(): Promise<void> {
    while (true) {
      const currentIndex = index;
      index += 1;

      if (currentIndex >= values.length) {
        return;
      }

      results[currentIndex] = await map(values[currentIndex], currentIndex);
    }
  }

  const workerCount = Math.min(limit, values.length);
  await Promise.all(Array.from({ length: workerCount }, () => worker()));
  return results;
}

async function scrapeDocsPage(url: string, count: number, total: number): Promise<ScrapeResult> {
  const jinaUrl = `https://r.jina.ai/http://${url.replace(/^https?:\/\//, "")}`;

  try {
    console.log(`Docs // Scrape // ${count}/${total} ${url}`);

    try {
      const markdown = stripJinaEnvelope(await fetchText(jinaUrl));
      if (markdown) {
        return {
          url,
          ok: true,
          markdown,
          mode: "jina",
        };
      }
    } catch {
      // Fall through to direct HTML scrape.
    }

    const html = await fetchText(url);
    const markdown = htmlToMarkdown(html);

    if (!markdown) {
      return {
        url,
        ok: false,
        error: "No markdown content returned from HTML fallback",
      };
    }

    return {
      url,
      ok: true,
      markdown,
      mode: "html",
    };
  } catch (error) {
    return {
      url,
      ok: false,
      error: error instanceof Error ? error.message : "Unknown scrape error",
    };
  }
}

function resolveOutputDirectory(repoRoot: string, outputArg?: string): string {
  if (!outputArg) {
    return resolve(repoRoot, DEFAULT_OUTPUT_DIR_RELATIVE);
  }

  const resolved = resolve(repoRoot, outputArg);
  if (resolved.endsWith(".md")) {
    return dirname(resolved);
  }

  return resolved;
}

function clearExistingExtFiles(outputDir: string): void {
  if (!existsSync(outputDir)) {
    return;
  }

  for (const entry of readdirSync(outputDir, { withFileTypes: true })) {
    if (!entry.isFile()) {
      continue;
    }

    if (!entry.name.endsWith(".ext.md")) {
      continue;
    }

    rmSync(resolve(outputDir, entry.name));
  }
}

const repoRoot = findRepoRoot(import.meta.dir);
const outputArg = Bun.argv[2];
const outputDir = resolveOutputDirectory(repoRoot, outputArg);
const outputDirRelative = outputDir.startsWith(repoRoot)
  ? outputDir.slice(repoRoot.length + 1)
  : outputDir;

mkdirSync(outputDir, { recursive: true });
clearExistingExtFiles(outputDir);

const requestedLanguage = (Bun.env.DOCS_LANGUAGE ?? DEFAULT_LANGUAGE).toLowerCase();
const discoveredUrls = await discoverDocsUrlsFromSitemap(SITEMAP_URL);
const docsUrls =
  requestedLanguage === "en" || requestedLanguage === "english"
    ? discoveredUrls.filter(isEnglishDocsUrl)
    : discoveredUrls;

if (docsUrls.length === 0) {
  throw new Error(`No docs URLs found from sitemap discovery (${SITEMAP_URL})`);
}

const scrapeResults = await mapConcurrent(docsUrls, CONCURRENCY, (url, index) =>
  scrapeDocsPage(url, index + 1, docsUrls.length),
);

const usedNames = new Map<string, number>();
const artifacts: PageArtifact[] = scrapeResults.map((result) => {
  const stem = fileStemFromUrl(result.url);
  const seen = usedNames.get(stem) ?? 0;
  usedNames.set(stem, seen + 1);

  const suffix = seen === 0 ? "" : `__${seen + 1}`;
  const fileName = `${stem}${suffix}.ext.md`;

  return {
    ...result,
    fileName,
    sourcePath: sourcePathFromUrl(result.url),
  };
});

const capturedAt = new Date().toISOString();

for (const artifact of artifacts) {
  const pagePath = resolve(outputDir, artifact.fileName);
  const pageContent = buildPageMarkdown(artifact, capturedAt);
  await Bun.write(pagePath, pageContent);
}

const indexContent = buildIndexMarkdown(artifacts, capturedAt, outputDirRelative);
await Bun.write(resolve(outputDir, "index.ext.md"), indexContent);

const successCount = artifacts.filter((artifact) => artifact.ok).length;
const failureCount = artifacts.length - successCount;
const blockedPages = artifacts
  .filter((artifact) => !artifact.ok)
  .map((artifact) => artifact.sourcePath)
  .join(",");

console.log(
  `Docs // Scrape // Wrote split external docs (source=${SOURCE_KEY},lang=${requestedLanguage},pages=${artifacts.length},ok=${successCount},failed=${failureCount},blocked=${blockedPages || "none"},dir=${outputDir})`,
);

